// This JS is included in all documentation .md files to make it work on the
// Gramex Guide at https://learn.gramener.com/guide/g1/

// Change all `*.md` links to `*`
$('a').each(function () {
  var href = $(this).attr('href')
  if (href.match(/\.md\b/))
    $(this).attr('href', href.replace(/\.md\b/, ''))
})
